import React, {useState} from "react";

export default function Clock() {

    const [sessionLen, setSessionLen] = useState(0);

    function updateSessionLen(event) {
        if (event.target.value === '-') {
            setSessionLen(sessionLen - 1)
        } else if (event.target.value === '+') {
            setSessionLen(sessionLen + 1)
        }
    }

    return (
        <div id="clock">
            <h1>25 + 5 Clock</h1>

            <section id="input-section">
                <div id="break-input">
                    <h3 className="timer-input">Break Length</h3>
                    <button className="less-time" id="break-less-time">-</button>
                    <p className="duration">5</p>
                    <button className="more-time" id="break-more-time">-</button>
                </div>
                <div id="session-input">
                    <h3 className="timer-input">Session Length</h3>
                    <button className="less-time" id="session-less-time" value="-">-</button>
                    <p className="duration">{sessionLen}</p>
                    <button className="more-time" id="session-more-time" value="+">-</button>
                </div>
            </section>

            <section id="timer">
                <h3 id="timer-title">Session</h3>
                <p>25:00</p>
                <div id="timer-ctrl">
                    <button>play/pause</button>
                    <button>reset</button>
                </div>
            </section>

        </div>
    )
}
