import React, { useState, useEffect } from "react";
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import {faPause, faPlay, faRotateLeft} from '@fortawesome/free-solid-svg-icons'

const SESSION = 'SESSION';
const BREAK = 'BREAK'
const timerIniState = {
    SESSION: {
        total: 1500,
        durationLeft: 1500,
        isPaused: true
    },
    BREAK: {
        total: 300,
        durationLeft: 300,
        isPaused: true
    }
}

export default function Clock() {

    const [sessionLen, setSessionLen] = useState({total: 1500, durationLeft: 1500, isPaused: true});
    const [breakLen, setBreakLen] = useState({total: 300, durationLeft: 300, isPaused: true});

    const [timers, setTimers] = useState(timerIniState);
    const [currentTimer, setCurrentTimer] = useState(SESSION);
    const [isRunning, setIsRunning] = useState(false);


    function resetAll() {
        console.log('Clicked on reset')
        setTimers(timerIniState)
        setCurrentTimer(SESSION)
        setIsRunning(false)
    }

    function updateTimer(event) {
        let target = event.target.id.split('-')[0].toUpperCase();   // Determine if target is session or break
        let val = event.target.value;

        if (!isRunning) {

            // Decrement target duration if duration is more than 1 minute
            if (val === '-' && timers[target].total > 60) {         
                setTimers(prev => {
                    let newTimer = {...prev}
                    newTimer[target].total = newTimer[target].total - 60;
                    newTimer[target].durationLeft = newTimer[target].durationLeft - 60;
                    return newTimer
                })

            // Increment target duration if duration is less than 60 mins
            } else if (val === '+' && timers[target].total < 3600) {       
                setTimers(prev => {
                    let newTimer = {...prev}
                    newTimer[target].total = newTimer[target].total + 60;
                    newTimer[target].durationLeft = newTimer[target].durationLeft + 60;
                    return newTimer
                })
            }
        }
    }   

    function runTimer() {
        setIsRunning(!isRunning);

        switch(currentTimer) {
            case SESSION:
                setSessionLen({
                        ...sessionLen,
                        isPaused: !sessionLen.isPaused
                    })
                break;
            case BREAK:
                setBreakLen({
                        ...breakLen,
                        isPaused: !breakLen.isPaused
                    })
                break;
            default:
                console.log('Error: No matching timers')
        }
    }

    // Countdown for session
    useEffect(() => {
        const interval = setInterval(() => {
            if (sessionLen.durationLeft === 0) {
                clearInterval(interval)
                setCurrentTimer(BREAK)
                
                setBreakLen({
                    ...breakLen,
                    isPaused: false
                })

                setSessionLen({
                    ...sessionLen,
                    durationLeft: sessionLen.total,
                    isPaused: true
                })

            } else if (!sessionLen.isPaused) {
                setSessionLen({
                        ...sessionLen,
                        durationLeft: sessionLen.durationLeft - 1
                    })
            }
        }, 1000);
        return () => clearInterval(interval);
    })

    // Countdown for break
    useEffect(() => {
        if (breakLen.durationLeft === 0) {
            console.log('finished break')
        } else if (!breakLen.isPaused) {
            console.log('counting break')
        }

        const interval2 = setInterval(() => {
            if (breakLen.durationLeft === 0) {
                clearInterval(interval2)
                setBreakLen({
                        ...breakLen,
                        durationLeft: breakLen.total,
                        isPaused: true
                    })

                setSessionLen({
                    ...sessionLen,
                    isPaused: false
                })
                
                setCurrentTimer(SESSION)

            } else if (!breakLen.isPaused) {
                setBreakLen({
                        ...breakLen,
                        durationLeft: breakLen.durationLeft - 1
                    })
            }
        }, 1000);
        return () => clearInterval(interval2);
    })
    
    return (
        <div id="clock">
            <div id="decorative-top">
                <span></span>
                <span></span>
                <span></span>
                <span></span>
            </div>
            <h1 id="main-title">25 + 5 Clock</h1>

            <section id="input-section">
                <div id="session-input">
                    <h3 id="session-label" className="length-label">Session (min)</h3>
                    <div className="input-container">
                        <button className="decrement" id="session-decrement" value="-" onClick={updateTimer}>-</button>
                        <p className="duration" id="session-length">{Math.floor(timers.SESSION.total / 60)}</p>
                        <button className="increment" id="session-increment" value="+" onClick={updateTimer}>+</button>
                    </div>
                </div>

                <div id="break-input">
                    <h3 id="break-label" className="length-label">Break (min)</h3>
                    <div className="input-container">
                        <button className="decrement" id="break-decrement" value="-" onClick={updateTimer}>-</button>
                        <p className="duration" id="break-length">{Math.floor(timers.BREAK.total / 60)}</p>
                        <button className="increment" id="break-increment" value="+" onClick={updateTimer}>+</button>
                    </div>
                </div>
            </section>

            <section id="timer">
                <div id="timer-container">
                    <h3 id="timer-label">{currentTimer}</h3>
                    <p id="time-left">{(
                        Math.floor((currentTimer===SESSION ? sessionLen.durationLeft : breakLen.durationLeft) / 60)).toLocaleString('en-US', {minimumIntegerDigits: 2})}:{
                        ((currentTimer===SESSION ? sessionLen.durationLeft : breakLen.durationLeft) % 60).toLocaleString('en-US', {minimumIntegerDigits: 2})}</p>
                    <div id="timer-ctrl">
                        <button onClick={runTimer} id="start_stop"><FontAwesomeIcon icon={isRunning ? faPause : faPlay} /></button>
                        <button id="reset" onClick={resetAll}><FontAwesomeIcon icon={faRotateLeft} /></button>
                    </div>
                </div>
            </section>
        </div>
    )
}

