import React, {useState, useEffect} from "react";

const SESSION = 'SESSION';
const BREAK = 'BREAK'

export default function Clock() {

    const [sessionLen, setSessionLen] = useState({total: 1500, durationLeft: 1500, isPaused: true});

    const [breakLen, setBreakLen] = useState({total: 300, durationLeft: 300, isPaused: true});
    const [currentTimer, setCurrentTimer] = useState(SESSION)


    function resetAll() {
        setBreakLen({total: 300, durationLeft: 300, isPaused: true});
        setSessionLen({total: 1500, durationLeft: 1500, isPaused: true});
        setCurrentTimer(SESSIOn)
    }

    function updateSessionLen(event) {
        if (event.target.value === '-' && sessionLen.total > 60) {
            setSessionLen(prev => {
                return {
                    ...prev,
                    total: prev.total - 60,
                    durationLeft: Math.floor(prev.total - 60)
                }
            })
        } else if (event.target.value === '+') {
            setSessionLen(prev => {
                return {
                    ...prev,
                    total: prev.total + 60,
                    durationLeft: Math.floor(prev.total + 60)
                }
            })
        }
    }

    function updateBreakLen(event) {
        if (event.target.value === '-' && breakLen.total > 60) {
            setBreakLen(prev => {
                return {
                    ...prev,
                    total: prev.total - 60,
                    durationLeft: Math.floor(prev.total - 60)
                }
            })
        } else if (event.target.value === '+') {
            setBreakLen(prev => {
                return {
                    ...prev,
                    total: prev.total + 60,
                    durationLeft: Math.floor(prev.total + 60)
                }
            })
        }
    }

    function runTimer() {
        switch(currentTimer) {
            case SESSION:
                setSessionLen(prev => {
                    return {
                        ...prev,
                        isPaused: !prev.isPaused
                    }
                })
                break;
            case BREAK:
                setBreakLen(prev => {
                    return {
                        ...prev,
                        isPaused: !prev.isPaused
                    }
                })
                break;
        }
    }

    useEffect(() => {
        const interval = setInterval(() => {
            if (sessionLen.durationLeft == 0) {
                clearInterval(interval)
                breakLen.isPaused = false;
                setCurrentTimer(BREAK)
            } else if (!sessionLen.isPaused) {
                setSessionLen(prevLen => {
                    return {
                        ...prevLen,
                        durationLeft: prevLen.durationLeft - 1
                    }
                })
            }
        }, 1000);
        return () => clearInterval(interval);
    })

    useEffect(() => {
        const interval = setInterval(() => {
            if (breakLen.durationLeft == 0) {
                clearInterval(interval)
            } else if (!breakLen.isPaused) {
                setBreakLen(prevLen => {
                    return {
                        ...prevLen,
                        durationLeft: prevLen.durationLeft - 1
                    }
                })
            }
        }, 1000);
        return () => clearInterval(interval);
    })

    return (
        <div id="clock">
            <h1>25 + 5 Clock</h1>

            <section id="input-section">
                <div id="break-input">
                    <h3 className="break-label">Break Length</h3>
                    <button className="decrement" id="break-decrement" value="-" onMouseDown={updateBreakLen}>-</button>
                    <p className="duration" id="break-length">{Math.floor(breakLen.total / 60)}</p>
                    <button className="increment" id="break-increment" value="+" onMouseDown={updateBreakLen}>+</button>
                </div>
                <div id="session-input">
                    <h3 className="session-label">Session Length</h3>
                    <button className="decrement" id="session-decrement" value="-" onMouseDown={updateSessionLen}>-</button>
                    <p className="duration" id="session-length">{Math.floor(sessionLen.total / 60)}</p>
                    <button className="increment" id="session-increment" value="+" onMouseDown={updateSessionLen}>+</button>
                </div>
            </section>

            <section id="timer">
                <h3 id="timer-label">Session</h3>
                <p id="time-left">{Math.floor(sessionLen.durationLeft/60)}:{(sessionLen.durationLeft - Math.floor(sessionLen.durationLeft/60)*60).toLocaleString('en-US', {minimumIntegerDigits: 2})}</p>
                <p>{Math.floor(breakLen.durationLeft/60)}:{(breakLen.durationLeft - Math.floor(breakLen.durationLeft/60)*60).toLocaleString('en-US', {minimumIntegerDigits: 2})}</p>
                <div id="timer-ctrl">
                    <button onMouseDown={runTimer} id="start_stop">play/pause</button>
                    <button id="reset">reset</button>
                </div>
            </section>

        </div>
    )
}
