import React, {useState, useEffect} from "react";

export default function Clock() {

    const [sessionLen, setSessionLen] = useState({total: 1500, durationLeft: 1500});

    const [breakLen, setBreakLen] = useState({total: 300, durationLeft: 300});
    const [isRunning, setIsRunning] = useState(true)

    function updateSessionLen(event) {
        if (event.target.value === '-' && sessionLen.total > 60) {
            setSessionLen({
                total: sessionLen.total - 60,
                durationLeft: Math.floor(sessionLen.total - 60)
            })
        } else if (event.target.value === '+') {
            setSessionLen({
                total: sessionLen.total + 60,
                durationLeft: Math.floor(sessionLen.total + 60)
            })
        }
    }

    function updateBreakLen(event) {
        if (event.target.value === '-' && breakLen.total > 60) {
            setBreakLen({
                total: breakLen.total - 60,
                durationLeft: Math.floor(breakLen.total - 60)
            })
        } else if (event.target.value === '+') {
            setBreakLen({
                total: breakLen.total + 60,
                durationLeft: Math.floor(breakLen.total + 60)
            })
        }
    }

    function runTimer() {
        setIsRunning(!isRunning)
    }

    useEffect(() => {
        const interval = setInterval(() => {
            if (sessionLen.durationLeft == 0) {
                clearInterval(interval)
            } else if (!isRunning) {
                setSessionLen(prevLen => {
                    return {
                        ...prevLen,
                        durationLeft: prevLen.durationLeft - 1
                    }
                })
            }
        }, 1000);
        return () => clearInterval(interval);
    })

    useEffect(() => {
        const interval = setInterval(() => {
            if (breakLen.durationLeft == 0) {
                clearInterval(interval)
            } else if (!isRunning) {
                setBreakLen(prevLen => {
                    return {
                        ...prevLen,
                        durationLeft: prevLen.durationLeft - 1
                    }
                })
            }
        }, 1000);
        return () => clearInterval(interval);
    })

    return (
        <div id="clock">
            <h1>25 + 5 Clock</h1>

            <section id="input-section">
                <div id="break-input">
                    <h3 className="timer-input">Break Length</h3>
                    <button className="less-time" id="break-less-time" value="-" onMouseDown={updateBreakLen}>-</button>
                    <p className="duration">{Math.floor(breakLen.total / 60)}</p>
                    <button className="more-time" id="break-more-time" value="+" onMouseDown={updateBreakLen}>+</button>
                </div>
                <div id="session-input">
                    <h3 className="timer-input">Session Length</h3>
                    <button className="less-time" id="session-less-time" value="-" onMouseDown={updateSessionLen}>-</button>
                    <p className="duration">{Math.floor(sessionLen.total / 60)}</p>
                    <button className="more-time" id="session-more-time" value="+" onMouseDown={updateSessionLen}>+</button>
                </div>
            </section>

            <section id="timer">
                <h3 id="timer-title">Session</h3>
                <p>{Math.floor(sessionLen.durationLeft/60)}:{(sessionLen.durationLeft - Math.floor(sessionLen.durationLeft/60)*60).toLocaleString('en-US', {minimumIntegerDigits: 2})}</p>
                <p>{Math.floor(breakLen.durationLeft/60)}:{(breakLen.durationLeft - Math.floor(breakLen.durationLeft/60)*60).toLocaleString('en-US', {minimumIntegerDigits: 2})}</p>
                <div id="timer-ctrl">
                    <button onMouseDown={runTimer}>play/pause</button>
                    <button>reset</button>
                </div>
            </section>

        </div>
    )
}
