import React, {useState, useEffect} from "react";

export default function Clock() {

    const [sessionLen, setSessionLen] = useState({total: 10, durationLeft: 10});

    const [breakLen, setBreakLen] = useState(0);
    const [isRunning, setIsRunning] = useState(true)

    function updateSessionLen(event) {
        if (event.target.value === '-' && sessionLen > 60) {
            setSessionLen(prevLen => {
                return {
                    ...prevLen,
                    durationLeft: prevLen.durationLeft - 1
                }
            })
        } else if (event.target.value === '+') {
            setSessionLen(sessionLen + 60)
        }
    }

    function runTimer() {
        setIsRunning(!isRunning)
    }

    useEffect(() => {
        const interval = setInterval(() => {
            if (sessionLen.durationLeft == 0) {
                clearInterval(interval)
            } else if (!isRunning) {
                setSessionLen(prevLen => {
                    return {
                        ...prevLen,
                        durationLeft: prevLen.durationLeft - 1
                    }
                })
            }
        }, 1000);
        return () => clearInterval(interval);
    })

    return (
        <div id="clock">
            <h1>25 + 5 Clock</h1>

            <section id="input-section">
                <div id="break-input">
                    <h3 className="timer-input">Break Length</h3>
                    <button className="less-time" id="break-less-time">-</button>
                    <p className="duration">5</p>
                    <button className="more-time" id="break-more-time">+</button>
                </div>
                <div id="session-input">
                    <h3 className="timer-input">Session Length</h3>
                    <button className="less-time" id="session-less-time" value="-" onMouseDown={updateSessionLen}>-</button>
                    <p className="duration">{Math.floor(sessionLen.total / 60)}</p>
                    <button className="more-time" id="session-more-time" value="+" onMouseDown={updateSessionLen}>+</button>
                </div>
            </section>

            <section id="timer">
                <h3 id="timer-title">Session</h3>
                <p>{Math.floor(sessionLen/60)}:{(sessionLen - Math.floor(sessionLen/60)*60).toLocaleString('en-US', {minimumIntegerDigits: 2})}</p>
                <div id="timer-ctrl">
                    <button onMouseDown={runTimer}>play/pause</button>
                    <button>reset</button>
                </div>
            </section>

        </div>
    )
}
