import React, {useState, useEffect} from "react";

const SESSION = 'SESSION';
const BREAK = 'BREAK'

export default function Clock() {

    const [sessionLen, setSessionLen] = useState({total: 1500, durationLeft: 1500, isPaused: true});

    const [breakLen, setBreakLen] = useState({total: 300, durationLeft: 300, isPaused: true});
    const [currentTimer, setCurrentTimer] = useState(SESSION);
    const [isRunning, setIsRunning] = useState(false);


    function resetAll() {
        console.log('Clicked on reset')
        setBreakLen({total: 300, durationLeft: 300, isPaused: true});
        setSessionLen({total: 1500, durationLeft: 1500, isPaused: true});
        setCurrentTimer(SESSION)
        setIsRunning(false)
    }

    function updateSessionLen(event) {
        console.log('Changed duration')
        if (!isRunning) {
            if (event.target.value === '-' && sessionLen.total > 60) {
                setSessionLen(prev => {
                    return {
                        ...prev,
                        total: prev.total - 60,
                        durationLeft: Math.floor(prev.total - 60)
                    }
                })
            } else if (event.target.value === '+'  && sessionLen.total < 3600) {
                setSessionLen(prev => {
                    return {
                        ...prev,
                        total: prev.total + 60,
                        durationLeft: Math.floor(prev.total + 60)
                    }
                })
            }
        }
    }

    function updateBreakLen(event) {
        if (!isRunning) {
            if (event.target.value === '-' && breakLen.total > 60) {
                setBreakLen(prev => {
                    return {
                        ...prev,
                        total: prev.total - 60,
                        durationLeft: Math.floor(prev.total - 60)
                    }
                })
            } else if (event.target.value === '+'  && breakLen.total < 3600) {
                setBreakLen(prev => {
                    return {
                        ...prev,
                        total: prev.total + 60,
                        durationLeft: Math.floor(prev.total + 60)
                    }
                })
            }
        }
    }

    function runTimer() {
        setIsRunning(!isRunning);

        switch(currentTimer) {
            case SESSION:
                setSessionLen(prev => {
                    return {
                        ...prev,
                        isPaused: !prev.isPaused
                    }
                })
                break;
            case BREAK:
                setBreakLen(prev => {
                    return {
                        ...prev,
                        isPaused: !prev.isPaused
                    }
                })
                break;
        }
    }

    useEffect(() => {
        const interval = setInterval(() => {
            if (sessionLen.durationLeft === 0) {
                clearInterval(interval)
                breakLen.isPaused = false;
                setCurrentTimer(BREAK)
                setIsRunning(false)
            } else if (!sessionLen.isPaused) {
                setSessionLen(prevLen => {
                    return {
                        ...prevLen,
                        durationLeft: prevLen.durationLeft - 1
                    }
                })
            }
        }, 1000);
        return () => clearInterval(interval);
    })

    useEffect(() => {
        const interval = setInterval(() => {
            if (breakLen.durationLeft === 0) {
                clearInterval(interval)
                setIsRunning(false)
                setBreakLen(prev => {
                    return {
                        ...prev,
                        durationLeft: prev.total,
                        isPaused: true
                    }
                })
                setSessionLen(prev => {
                    return {
                        ...prev,
                        durationLeft: prev.total,
                        isPaused: false
                    }
                })
                setCurrentTimer(SESSION)
            } else if (!breakLen.isPaused) {
                setBreakLen(prevLen => {
                    return {
                        ...prevLen,
                        durationLeft: prevLen.durationLeft - 1
                    }
                })
            }
        }, 1000);
        return () => clearInterval(interval);
    })

    return (
        <div id="clock">
            <h1>25 + 5 Clock</h1>

            <section id="input-section">
                <div id="break-input">
                    <h3 id="break-label">Break Length</h3>
                    <button className="decrement" id="break-decrement" value="-" onClick={updateBreakLen}>-</button>
                    <p className="duration" id="break-length">{Math.floor(breakLen.total / 60)}</p>
                    <button className="increment" id="break-increment" value="+" onClick={updateBreakLen}>+</button>
                </div>

                <div id="session-input">
                    <h3 id="session-label">Session Length</h3>
                    <button className="decrement" id="session-decrement" value="-" onClick={updateSessionLen}>-</button>
                    <p className="duration" id="session-length">{Math.floor(sessionLen.total / 60)}</p>
                    <button className="increment" id="session-increment" value="+" onClick={updateSessionLen}>+</button>
                </div>
            </section>

            <section id="timer">
                <h3 id="timer-label">{currentTimer}</h3>
                <p id="time-left">{(
                    Math.floor((currentTimer===SESSION ? sessionLen.durationLeft : breakLen.durationLeft) / 60)).toLocaleString('en-US', {minimumIntegerDigits: 2})}:{
                    ((currentTimer===SESSION ? sessionLen.durationLeft : breakLen.durationLeft) % 60).toLocaleString('en-US', {minimumIntegerDigits: 2})}</p>
                <p>{Math.floor(breakLen.durationLeft/60)}:{(breakLen.durationLeft%60).toLocaleString('en-US', {minimumIntegerDigits: 2})}</p>
                <div id="timer-ctrl">
                    <button onMouseDown={runTimer} id="start_stop">play/pause</button>
                    <button id="reset" onClick={resetAll}>reset</button>
                </div>
            </section>

        </div>
    )
}

